export type FontSizeOptions = 'sm' | 'md' | 'lg' | 'xl'
export type FontSizeProps = {
  $lgFontSize?: FontSizeOptions
  $mdFontSize?: FontSizeOptions
  $smFontSize?: FontSizeOptions
}

export type RemProps = {
  $lgRem?: number
  $mdRem?: number
  $smRem?: number
}
export type PercentProps = {
  $lgPercent?: number
  $mdPercent?: number
  $smPercent?: number
}
export type VwProps = {
  $lgVw?: number
  $mdVw?: number
  $smVw?: number
}
export type VhProps = {
  $lgVh?: number
  $mdVh?: number
  $smVh?: number
}
